class Forme { public: virtual double aire() const = 0; };
class Cercle : public Forme {
  double r; public: Cercle(double r) : r(r) {}
  double aire() const override { return 3.14 * r * r; }
};

double div(double a, double b) {
  if(b == 0) throw std::invalid_argument("Zero!");
  return a / b;
}

